import React, {Suspense, lazy, useState} from "react";
import {Button, Container, Form} from "react-bootstrap";
import {useParams} from "react-router-dom";
import PointerBoard from "./PointerBoard";
import SockJS from 'sockjs-client';
import Stomp from 'webstomp-client';
import Config from "../../Config";



const SessionLogin = () => {
  const params = useParams();
  const [show, setShow] = React.useState(false);
  const [error, setError] = React.useState(false);
  const [name, setName] = React.useState('');
  const [stompClient, setStompClient] = useState(null);


  const joinAsAPointerOnClicked = () => {
    if (name == null || name == "" || name.trim() == "") {
      setError(true);
      return;
    } else {
      connectWork();
    }

  }

  function connectWork() {
    const stompClient = Stomp.over(SockJS(Config.WS_URL))
    stompClient.connect({},
      () => {
        setStompClient(stompClient);
        stompClient.subscribe('/topic/public', onMessageReceived);
        // Tell your username to the server
        stompClient.send("/app/addUser",
          JSON.stringify({sender: name, type:"JOIN"}),
          {sender: "Sushil", type: "JOIN"}
        )
      }, onError)
  }

  function onMessageReceived(payload) {
    let message = JSON.parse(payload.body);
    if(message.type == "DESCRIPTION"){
      //setMessage(message.content)
    }
  }

  function onError(error) {
    //connectingElement.textContent = 'Could not connect to WebSocket server. Please refresh this page to try again!';
    //connectingElement.style.color = 'red';
    console.log("error");
    console.log(error);
  }

  const joinAsAObserverOnClicked = () => {
    alert("not implemented")
  }

  return(
    <Container>
      {
        !show ? <>
            <h1>Session Login</h1>
            <div>
              <Form.Label htmlFor="name">Name:</Form.Label>
              <Form.Control
                type="text"
                id="name"
                placeholder={"Enter Your Name"}
                onChange={(e) => setName(e.target.value)}
              />
              <Form.Text id="passwordHelpBlock"  hidden={!error}>
                Enter Your Name
              </Form.Text>
            </div>
            <div>
              <Button variant="primary" onClick={() => joinAsAPointerOnClicked()}>Join as a Pointer</Button>
              <Button variant="primary" onClick={() => joinAsAObserverOnClicked()}>Join as a Observer</Button>
            </div>
        </>
          :
            <PointerBoard />
      }

    </Container>
  )
}

export default SessionLogin;
