import React, {useEffect} from "react";
import {Button, Container, Form, Table} from "react-bootstrap";
import {useParams} from "react-router-dom";
import config from "../../Config";
import { Client } from '@stomp/stompjs';
import { WebSocket } from 'ws';
//Object.assign(global, { WebSocket });



const PointerVote = () => {
    const [series, setSeries] = React.useState([]);
    const [message, setMessage] = React.useState('');
    const [messages, setMessages] = React.useState([]);
    const [webSocket, setWebSocket] = React.useState(null);
    const params = useParams();


    const connect = () => {
        //const webSocket = new WebSocket(config.WS_URL);
        const client = new Client({
            brokerURL: config.WS_URL,
            onConnect: () => {
                client.subscribe('/app/addUser', message =>
                    console.log(`Received: ${message.body}`)
                );
                client.publish({ destination: '/topic/publish', body: 'First Message' });
            },
        });

        client.activate();
    };

    useEffect(() => {
        connect();
    }, []);

    const handleSend = () => {
        const messageObj = { sender: 'Me', content: message };
        const messageString = JSON.stringify(messageObj);
        webSocket.send(messageString);
        setMessage('');
    };

    // return (
    //     <div>
    //         {messages.map((message) => (
    //             <div key={message.id}>
    //                 {message.sender}: {message.content}
    //             </div>
    //         ))}
    //         <input
    //             type="text"
    //             value={message}
    //             onChange={(event) => setMessage(event.target.value)}
    //         />
    //         <button onClick={handleSend}>Send</button>
    //     </div>
    // );

    useEffect(() => {
        const fibonacciResult = number => {
            let n1 = 0, n2 = 1, nextTerm;
            let seq = [n1,n2];
            nextTerm = n1 + n2;
            while (nextTerm <= number) {
                // print the next term
                seq.push(nextTerm)
                n1 = n2;
                n2 = nextTerm;
                nextTerm = n1 + n2;
            }
            return seq;
        };
        setSeries(fibonacciResult(100));
    }, [])


    return (
        <Container>
            <div>
                <Form.Label htmlFor="inputPassword5">Story Description</Form.Label>
                <Form.Control
                    type="textarea"
                    id="inputPassword5"
                    aria-describedby="passwordHelpBlock"
                />
                <Form.Text id="passwordHelpBlock" muted>
                    Enter Your Story
                </Form.Text>
            </div>
            <div style={{display: "flex", justifyContent: "space-between"}}>
                <Button variant="danger">Clear Vote</Button>
                <Button variant="primary">Show Vote</Button>
            </div>
            {/*point part*/}
            <div>
                {
                    series.map((item, key) => {
                        return (
                            <Button variant="secondary" key={key}>{item}</Button>
                        )
                    })
                }
            </div>
            <hr/>
            <div>
                <p>Score</p>
                <div>
                    <Table striped bordered hover>
                        <thead>
                        <tr>
                            <th>#</th>
                            <th>Name</th>
                            <th>Score</th>
                        </tr>
                        </thead>
                        <tbody>
                        <tr>
                            <td>1</td>
                            <td>Otto</td>
                            <td>1</td>
                        </tr>
                        <tr>
                            <td>2</td>
                            <td>Thornton</td>
                            <td>3</td>
                        </tr>
                        <tr>
                            <td>3</td>
                            <td>Larry the Bird</td>
                            <td>2</td>
                        </tr>
                        </tbody>
                    </Table>
                </div>
            </div>

        </Container>
    )
}


export default PointerVote;
